package status

import (
	"github.com/golangmc/minecraft-server/apis/data"
	"github.com/golangmc/minecraft-server/apis/data/chat"
)

const (
	ServerMotd = "&bMy super go server"
	ServerIcon = " data:image/png;base64,"
)

type Response struct {
	Version     Version `json:"version,string"`
	Players     Players `json:"players,string"`
	Description Message `json:"description"`
	Favicon     string  `json:"favicon"`
}

type Version struct {
	Name     string `json:"name"`
	Protocol int    `json:"protocol"`
}

type Players struct {
	Max    int            `json:"max"`
	Online int            `json:"online"`
	Sample []SamplePlayer `json:"sample"`
}

type SamplePlayer struct {
	Name string `json:"name"`
	ID   string `json:"id"`
}

type Message struct {
	Text string `json:"text"`
}

func DefaultResponse() Response {
	return Response{
		Version: Version{
			Name:     "1.15.2",
			Protocol: data.CurrentProtocol.Protocol(),
		},
		Players: Players{
			Max:    10,
			Online: 0,
			Sample: []SamplePlayer{},
		},
		Description: Message{
			Text: chat.Translate(ServerMotd),
		},
		Favicon: ServerIcon,
	}
}
